}

// Example 2
// Immutability: creating a new array instead of modifying the original.
const numbers = [1, 2, 3];

// Instead of numbers.push(4) (mutation)
const newNumbers = [...numbers, 4]; // Create a new array

console.log(numbers);     // [1, 2, 3]
console.log(newNumbers);  // [1, 2, 3, 4]

