// Example 1
// Setting a simple cookie that expires in one day.
const expires = new Date(Date.now() + 86400e3).toUTCString();
document.cookie = `username=Alice; expires=${expires}; path=/`;
console.log('Cookie set.');

// Example 2
// A helper function to get a cookie by name (as the native API is poor).
function getCookie(name) {
  const value = `; ${document.cookie}`;
  const parts = value.split(`; ${name}=`);
  if (parts.length === 2) return parts.pop().split(';').shift();
}

console.log(`Username from cookie: ${getCookie('username')}`);

// Example 1
// Getting the user's current position.
if ('geolocation' in navigator) {
  navigator.geolocation.getCurrentPosition(
    (position) => {
      const { latitude, longitude } = position.coords;
      console.log(`Latitude: ${latitude}, Longitude: ${longitude}`);
    },
    (error) => {
      console.error(`Error getting location: ${error.message}`);
    }
  );
} else {
  console.log('Geolocation is not available in this browser.');
}

