// Assume the following HTML: <canvas id="myCanvas" width="200" height="100"></canvas>

// Example 1
const canvas = document.getElementById('myCanvas');
if (canvas) {
  const ctx = canvas.getContext('2d');
  // Draw a red rectangle
  ctx.fillStyle = 'red';
  ctx.fillRect(10, 10, 150, 80);
}

// Example 2
// Draw a blue circle.
if (canvas) {
  const ctx = canvas.getContext('2d');
  ctx.beginPath();
  ctx.arc(100, 50, 40, 0, 2 * Math.PI);
  ctx.fillStyle = 'blue';
  ctx.fill();
}

